="layui-tab-content"><fieldset><legend>条件搜索</legend><form class="layui-form layui-form-pane form-search" action="<?php echo sysuri(); ?>" onsubmit="return false" method="get" autocomplete="off"><div class="layui-form-item layui-inline"><label class="layui-form-label">登录账号</label><label class="layui-input-inline"><input name="username" value="<?php echo htmlentities((isset($get['username']) && ($get['username'] !== '')?$get['username']:''),ENT_QUOTES); ?>" placeholder="请输入登录账号" class="layui-input"></label></div><div class="layui-form-item layui-inline"><label class="layui-form-label">用户名称</label><label class="layui-input-inline"><input name="nickname" value="<?php echo htmlentities((isset($get['nickname']) && ($get['nickname'] !== '')?$get['nickname']:''),ENT_QUOTES); ?>" placeholder="请输入用户名称" class="layui-input"></label></div><!--<?php if(!(empty($bases) || (($bases instanceof \think\Collection || $bases instanceof \think\Paginator ) && $bases->isEmpty()))): ?>--><div class="layui-form-item layui-inline"><label class="layui-form-label">角色身份</label><div class="layui-input-inline"><select name="usertype" lay-search class="layui-select"><option value=''>-- 全部 --</option><?php foreach($bases as $base): if($base['code'] == input('get.usertype')): ?><option selected value="<?php echo htmlentities((isset($base['code']) && ($base['code'] !== '')?$base['code']:''),ENT_QUOTES); ?>"><?php echo htmlentities((isset($base['name']) && ($base['name'] !== '')?$base['name']:''),ENT_QUOTES); ?> ( <?php echo htmlentities((isset($base['code']) && ($base['code'] !== '')?$base['code']:''),ENT_QUOTES); ?> )</option><?php else: ?><option value="<?php echo htmlentities((isset($base['code']) && ($base['code'] !== '')?$base['code']:''),ENT_QUOTES); ?>"><?php echo htmlentities((isset($base['name']) && ($base['name'] !== '')?$base['name']:''),ENT_QUOTES); ?> ( <?php echo htmlentities((isset($base['code']) && ($base['code'] !== '')?$base['code']:''),ENT_QUOTES); ?> )</option><?php endif; ?><?php endforeach; ?></select></div></div><!--<?php endif; ?>--><div class="layui-form-item layui-inline layui-hide"><label class="layui-form-label">联系手机</label><label class="layui-input-inline"><input name="phone" value="<?php echo htmlentities((isset($get['phone']) && ($get['phone'] !== '')?$get['phone']:''),ENT_QUOTES); ?>" placeholder="请输入联系手机" class="layui-input"></label></div><div class="layui-form-item layui-inline layui-hide"><label class="layui-form-label">联系邮箱</label><label class="layui-input-inline"><input name="mail" value="<?php echo htmlentities((isset($get['mail']) && ($get['mail'] !== '')?$get['mail']:''),ENT_QUOTES); ?>" placeholder="请输入联系邮箱" class="layui-input"></label></div><div class="layui-form-item layui-inline"><label class="layui-form-label">最后登录</label><div class="layui-input-inline"><input data-date-range name="login_at" value="<?php echo htmlentities((isset($get['login_at']) && ($get['login_at'] !== '')?$get['login_at']:''),ENT_QUOTES); ?>" placeholder="请选择登录时间" class="layui-input"></div></div><div class="layui-form-item layui-inline"><label class="layui-form-label">创建时间</label><div class="layui-input-inline"><input data-date-range name="create_at" value="<?php echo htmlentities((isset($get['create_at']) && ($get['create_at'] !== '')?$get['create_at']:''),ENT_QUOTES); ?>" placeholder="请选择创建时间" class="layui-input"></div></div><div class="layui-form-item layui-inline"><input type="hidden" name="type" value="<?php echo htmlentities((isset($type) && ($type !== '')?$type:'index'),ENT_QUOTES); ?>"><button class="layui-btn layui-btn-primary"><i class="layui-icon">&#xe615;</i> 搜 索</button></div></form></fieldset><table id="UserTable" data-url="<?php echo sysuri('index'); ?>" data-target-search="form.form-search"></table></div></div><script>    $(function () {
        $('#UserTable').layTable({
            even: true, height: 'full',
            sort: {field: 'sort desc,id', type: 'desc'},
            where: {type: '<?php echo htmlentities((isset($type) && ($type !== '')?$type:"index"),ENT_QUOTES); ?>'},
            cols: [[
                {checkbox: true, fixed: true},
                {field: 'sort', title: '排序权重', width: 100, align: 'center', sort: true, templet: '#SortInputTpl'},
                {
                    field: 'headimg', title: '头像', width: 60, align: 'center', templet: function (d) {
                        if (!d.headimg) return '';
                        return layui.laytpl('<div class="headimg headimg-xs margin-0" data-tips-image data-tips-hover data-lazy-src="{{d.headimg}}"></div>').render(d);
                    }
                },
                {field: 'username', title: '登录账号', minWidth: 100, templet: '<div>{{d.username||"-"}}</div>'},
                {field: 'nickname', title: '用户名称', minWidth: 100, templet: '<div>{{d.nickname||"-"}}</div>'},
                /* <?php if(!(empty($bases) || (($bases instanceof \think\Collection || $bases instanceof \think\Paginator ) && $bases->isEmpty()))): ?> */
                {
                    field: 'usertype', title: '角色身份', minWidth: 140, sort: true, templet: function (d) {
                        d.userinfo = d.userinfo || {};
                        return d.userinfo.code ? (d.userinfo.name + ' ( ' + d.userinfo.code + ' ) ') : '';
                    }
                },
                /* <?php endif; ?> */
                // {field: 'contact_mail', title: '联系邮箱', minWidth: 80, templet: '<div>{{d.contact_mail||"-"}}</div>'},
                // {field: 'contact_phone', title: '联系电话', minWidth: 80, templet: '<div>{{d.contact_phone||"-"}}</div>'},
                {field: 'status', title: '账号状态', align: 'center', minWidth: 110, templet: '#StatusSwitchTpl'},
                {field: 'login_num', title: '登录次数', align: 'center', minWidth: 100, sort: true},
                {field: 'login_at', title: '最后登录', align: 'center', minWidth: 170, sort: true},
                {field: 'create_at', title: '创建时间', align: 'center', minWidth: 170, sort: true},
                {toolbar: '#toolbar', title: '操作面板', align: 'center', minWidth: 160, fixed: 'right'}
            ]]
        });

        // 数据状态切换操作
        layui.form.on('switch(StatusSwitch)', function (obj) {
            var data = {id: obj.value, status: obj.elem.checked > 0 ? 1 : 0};
            $.form.load("<?php echo url('state'); ?>", data, 'post', function (ret) {
                if (ret.code < 1) $.msg.error(ret.info, 3, function () {
                    $('#UserTable').trigger('reload');
                }); else $('#UserTable').trigger('reload')
                return false;
            }, false);
        });
    });
</script><!-- 数据状态切换模板 --><script type="text/html" id="StatusSwitchTpl"><!--<?php if(auth("state")): ?>--><input type="checkbox" value="{{d.id}}" lay-skin="switch" lay-text="已激活|已禁用" lay-filter="StatusSwitch" {{d.status>0?'checked':''}}><!--<?php else: ?>-->    {{d.status ? '<b class="color-green">已启用</b>' : '<b class="color-red">已禁用</b>'}}
    <!--<?php endif; ?>--></script><!-- 列表排序权重模板 --><script type="text/html" id="SortInputTpl"><input type="number" min="0" data-blur-number="0" data-action-blur="<?php echo sysuri(); ?>" data-value="id#{{d.id}};action#sort;sort#{value}" data-loading="false" value="{{d.sort}}" class="layui-input text-center"></script><script type="text/html" id="toolbar"><?php if(isset($type) and $type == 'index'): ?><!--<?php if(auth("edit")): ?>--><a class="layui-btn layui-btn-sm" data-title="编辑用户" data-modal='<?php echo url("edit"); ?>?id={{d.id}}'>编 辑</a><!--<?php endif; ?>--><!--<?php if(auth("pass")): ?>--><a class="layui-btn layui-btn-sm layui-btn-normal" data-title="设置密码" data-modal='<?php echo url("pass"); ?>?id={{d.id}}'>密 码</a><!--<?php endif; ?>--><?php else: ?><!--<?php if(auth("edit")): ?>--><a class="layui-btn layui-btn-sm" data-title="编辑用户" data-modal='<?php echo url("edit"); ?>?id={{d.id}}'>编 辑</a><!--<?php endif; ?>--><!--<?php if(auth("remove")): ?>--><a class="layui-btn layui-btn-sm layui-btn-danger" data-confirm="确定要永久删除此账号吗？" data-action="<?php echo url('remove'); ?>" data-value="id#{{d.id}}">删 除</a><!--<?php endif; ?>--><?php endif; ?></script></div></div></div>