<div class="layui-card"><?php if(!(empty($title) || (($title instanceof \think\Collection || $title instanceof \think\Paginator ) && $title->isEmpty()))): ?><div class="layui-card-header"><span class="layui-icon font-s10 color-desc margin-right-5">&#xe65b;</span><?php echo htmlentities((isset($title) && ($title !== '')?$title:''),ENT_QUOTES); ?><div class="pull-right"><!--<?php if(auth("add")): ?>--><button class='layui-btn layui-btn-sm layui-btn-primary' data-modal='<?php echo url("add"); ?>' data-title="添加分类">添加分类</button><!--<?php endif; ?>--><!--<?php if(auth("remove")): ?>--><button class='layui-btn layui-btn-sm layui-btn-primary' data-action='<?php echo url("remove"); ?>' data-rule="id#{key}">删除分类</button><!--<?php endif; ?>--></div></div><?php endif; ?><div class="layui-card-line"></div><div class="layui-card-body"><div class="layui-card-html"><div class="think-box-shadow"><?php if(!(empty($list) || (($list instanceof \think\Collection || $list instanceof \think\Paginator ) && $list->isEmpty()))): ?><table class="layui-table" lay-skin="line"><thead><tr><th class='list-table-check-td think-checkbox'><label><input data-auto-none data-check-target='.list-check-box' type='checkbox'></label></th><th class='list-table-sort-td'><button class="layui-btn layui-btn-xs" data-reload type="button">刷 新</button></th><th class='text-center'></th><th colspan="3"></th></tr></thead><tbody><?php foreach($list as $key=>$vo): ?><tr><td class='list-table-check-td think-checkbox'><label><input class="list-check-box" type='checkbox' value='<?php echo htmlentities($vo['ids'],ENT_QUOTES); ?>'></label></td><td class='list-table-sort-td'><label><input class="list-sort-input" data-action-blur="<?php echo sysuri(); ?>" data-loading="false" data-value="id#<?php echo htmlentities($vo['id'],ENT_QUOTES); ?>;action#sort;sort#{value}" value="<?php echo htmlentities($vo['sort'],ENT_QUOTES); ?>"></label></td><td class="nowrap"><span class="color-desc notselect"><?php echo $vo['spl']; ?></span><?php echo htmlentities($vo['name'],ENT_QUOTES); ?></td><td class="color-desc"><?php echo htmlentities((isset($vo['remark']) && ($vo['remark'] !== '')?$vo['remark']:''),ENT_QUOTES); ?></td><td class='text-center nowrap'><?php if($vo['status'] == '0'): ?><span class="color-red">已禁用</span><?php else: ?><span class="color-green">已激活</span><?php endif; ?></td><td class='text-center nowrap notselect'><!--<?php if(auth("add")): ?>--><a class="layui-btn layui-btn-xs layui-btn-primary" data-modal='<?php echo url("add"); ?>?pid=<?php echo htmlentities($vo['id'],ENT_QUOTES); ?>' data-title="添加子分类">添 加</a><!--<?php endif; ?>--><!--<?php if(auth("edit")): ?>--><a class="layui-btn layui-btn-xs" data-dbclick data-modal='<?php echo url("edit"); ?>?id=<?php echo htmlentities($vo['id'],ENT_QUOTES); ?>' data-title="编辑分类">编 辑</a><!--<?php endif; ?>--><!--<?php if($vo['status'] == 0 and auth("state")): ?>--><a class="layui-btn layui-btn-warm layui-btn-xs" data-action="<?php echo url('state'); ?>" data-csrf="<?php echo systoken('state'); ?>" data-value="id#<?php echo htmlentities($vo['ids'],ENT_QUOTES); ?>;status#1">激 活</a><!--<?php endif; ?>--><!--<?php if($vo['status'] == 1 and auth("state")): ?>--><a class="layui-btn layui-btn-warm layui-btn-xs" data-action="<?php echo url('state'); ?>" data-csrf="<?php echo systoken('state'); ?>" data-value="id#<?php echo htmlentities($vo['ids'],ENT_QUOTES); ?>;status#0">禁 用</a><!--<?php endif; ?>--><!--<?php if(auth("remove")): ?>--><a class="layui-btn layui-btn-danger layui-btn-xs" data-action="<?php echo url('remove'); ?>" data-confirm="确定要删除数据吗?" data-csrf="<?php echo systoken('remove'); ?>" data-value="id#<?php echo htmlentities($vo['ids'],ENT_QUOTES); ?>">删 除</a><!--<?php endif; ?>--></td></tr><?php endforeach; ?></tbody></table><?php echo (isset($pagehtml) && ($pagehtml !== '')?$pagehtml:''); else: ?><span class="notdata">没有记录哦</span><?php endif; ?></div></div></div></div>